mapper.select:
select b.batch_id, b.batch_name, b.part_type_id, b.quantity, b.production_line, b.dev_id, b.expected_start_time, b.expected_end_time, pt.part_type_name,b.create_by, b.create_time, b.remark, e.dev_name from sys_batch b inner join sys_part_type pt on b.part_type_id = pt.part_type_id inner join sys_equipment e on b.dev_id = e.dev_id where b.detect_status = '0' and b.del_flag = '0'
AND b.batch_id = #{batchId}
AND b.batch_name like concat('%', #{batchName}, '%')
AND b.part_type_id = #{partTypeId}
AND pt.part_type_name LIKE CONCAT('%', #{partTypeName}, '%')
AND b.quantity = #{quantity}
AND b.production_line like concat('%', #{productionLine}, '%')
AND b.dev_id = #{devId}
AND e.dev_name LIKE CONCAT('%', #{devName}, '%')
AND date_format(b.expected_start_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(b.expected_start_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

select b.batch_id, b.batch_name, b.part_type_id, b.quantity, b.dev_id, b.detect_start_time, b.detect_end_time, b.qualified_quantity, b.unqualified_quantity,b.qualified_rate, b.create_by, b.create_time, b.remark,b.expected_start_time, b.expected_end_time, b.production_line, pt.part_type_name, e.dev_name from sys_batch b inner join sys_part_type pt on b.part_type_id = pt.part_type_id inner join sys_equipment e on b.dev_id = e.dev_id where b.detect_status = '1' and b.del_flag = '0'
AND b.batch_id = #{batchId}
AND b.batch_name like concat('%', #{batchName}, '%')
AND b.part_type_id = #{partTypeId}
AND pt.part_type_name LIKE CONCAT('%', #{partTypeName}, '%')
AND b.quantity = #{quantity}
AND b.qualified_rate >= #{qualifiedRate}
AND b.dev_id = #{devId}
AND e.dev_name LIKE CONCAT('%', #{devName}, '%')
AND date_format(b.detect_start_time,'%y%m%d') >= date_format(#{params.beginTime},'%y%m%d')
AND date_format(b.detect_start_time,'%y%m%d') <= date_format(#{params.endTime},'%y%m%d')

mapper.update:
update sys_batch
batch_name = #{batchName}, part_type_id = #{partTypeId}, production_line = #{productionLine}, dev_id = #{devId}, expected_start_time = #{expectedStartTime}, expected_end_time = #{expectedEndTime}, detect_status = #{detectStatus}, detect_start_time = #{detectStartTime}, detect_end_time = #{detectEndTime}, quantity = #{quantity}, qualified_quantity = #{qualifiedQuantity}, unqualified_quantity = #{unqualifiedQuantity}, qualified_rate = #{qualifiedRate}, update_by = #{updateBy}, remark = #{remark}, update_time = sysdate()
where batch_id = #{batchId}
